type WebsocketProxy struct {
	Director func(*http.Request, http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (p *WebsocketProxy) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	backendURL := p.Backend(r)
	if backendURL == nil {
		http.Error(w, "Bad Gateway", http.StatusBadGateway)
		return
	}

	conn, err := p.Dialer.Dial(backendURL.String(), nil)
	if err != nil {
		http.Error(w, "Bad Gateway", http.StatusBadGateway)
		return
	}
	defer conn.Close()

	clientConn, err := p.Upgrader.Upgrade(w, r, nil)
	if err != nil {
		return
	}
	defer clientConn.Close()

	errChan := make(chan error, 2)
	go p.replicateWebsocketConn(clientConn, conn, errChan)
	go p.replicateWebsocketConn(conn, clientConn, errChan)

	select {
	case err := <-errChan:
		if err != nil {
			clientConn.WriteMessage(websocket.CloseMessage, []byte{})
			conn.WriteMessage(websocket.CloseMessage, []byte{})
		}
	}
}

func (p *WebsocketProxy) replicateWebsocketConn(src, dst *websocket.Conn, errChan chan error) {
	for {
		messageType, message, err := src.ReadMessage()
		if err != nil {
			errChan <- err
			return
		}

		err = dst.WriteMessage(messageType, message)
		if err != nil {
			errChan <- err
			return
		}
	}
}

