import (
	"context"
	"errors"
	"io"
	"os"
	"os/exec"
)

func copyViaOsascript(ctx context.Context, password string) error {
	r, w, err := os.Pipe()
	if err != nil {
		return err
	}
	defer r.Close()

	cmd := exec.CommandContext(ctx, "osascript", "-e", "set the clipboard to (read stdin)")
	cmd.Stdin = r

	errChan := make(chan error, 1)
	go func() {
		errChan <- cmd.Run()
	}()

	_, err = io.WriteString(w, password)
	if err != nil {
		w.Close()
		return err
	}

	w.Close()

	select {
	case err := <-errChan:
		if err != nil {
			return err
		}
	case <-ctx.Done():
		return ctx.Err()
	}

	clearCmd := exec.Command("osascript", "-e", "set the clipboard to \"\"")
	return clearCmd.Run()
}

