func helloHandler(w http.ResponseWriter, req *http.Request) {
	fmt.Fprintln(w, "hello")
}

func mirrorHandler(w http.ResponseWriter, req *http.Request) {
	var buf bytes.Buffer
	teed := io.TeeWriter(w, &buf)
	fmt.Fprintf(teed, "Method: %s\n", req.Method)
	fmt.Fprintf(teed, "URL: %s\n", req.URL.String())
	fmt.Fprintln(teed, "Headers:")
	req.Header.Write(teed)
	bodyBytes, _ := io.ReadAll(req.Body)
	req.Body.Close()
	req.Body = io.NopCloser(io.Reader(bytes.NewBuffer(bodyBytes)))
	fmt.Fprintf(teed, "Body: %s\n", string(bodyBytes))
	time.Sleep(10 * time.Millisecond)
	fmt.Print(buf.String())
}

func main() {
	http.HandleFunc("/hello", helloHandler)
	http.HandleFunc("/", mirrorHandler)

	for port := 9091; port <= 9097; port++ {
		go func(p int) {
			http.ListenAndServe(fmt.Sprintf(":%d", p), nil)
		}(port)
	}

	http.ListenAndServe(":9098", nil)
}

