func handler(w http.ResponseWriter, r *http.Request) {
	key := r.Header.Get("proxify")
	if resp, ok := responses[key]; ok {
		defer func() { delete(responses, key) }()
		for k, vv := range resp.Header {
			for _, v := range vv {
				w.Header().Add(k, v)
			}
		}
		w.WriteHeader(resp.StatusCode)
		resp.Write(w)
	} else {
