)

type Collector struct {
	PacketCount int64
}

func (c *Collector) GetPacketCount() int64 {
	if c == nil {
		return 0
	}
	return c.PacketCount
}

func ServeMetricsAt(addr string, c *Collector) {
	uptime := prometheus.NewGauge(prometheus.GaugeOpts{
		Name: "uptime_seconds",
		Help: "Uptime of the application in seconds.",
	})

	packetCount := prometheus.NewGauge(prometheus.GaugeOpts{
		Name: "packet_count_total",
		Help: "Total number of packets processed.",
	})

	prometheus.MustRegister(uptime)
	prometheus.MustRegister(packetCount)

	startTime := time.Now()

	http.HandleFunc("/metrics", func(w http.ResponseWriter, r *http.Request) {
		currentTime := time.Now()
		uptime.Set(currentTime.Sub(startTime).Seconds())
		packetCount.Set(float64(c.GetPacketCount()))
		promhttp.Handler().ServeHTTP(w, r)
	})

	go func() {
		http.ListenAndServe(addr, nil)
	}()
}

