type Example struct {
	Type string
	Link string
	Data string
}

func getExamples() []Example {
	return []Example{
		{Type: "demo", Link: "one", Data: "Example One Data"},
		{Type: "demo", Link: "two", Data: "Example Two Data"},
	}
}

func serve(addr string) error {
	examples := getExamples()
	tmpl := template.Must(template.ParseFiles("home.html", "example.html"))

	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		if r.URL.Path != "/" {
			http.NotFound(w, r)
			return
		}
		tmpl.ExecuteTemplate(w, "home.html", examples)
	})

	http.HandleFunc("/wasm_exec.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeFile(w, r, "wasm_exec.js")
	})

	http.HandleFunc("/example/", func(w http.ResponseWriter, r *http.Request) {
		parts := strings.Split(strings.TrimPrefix(r.URL.Path, "/example/"), "/")
		if len(parts) < 2 {
			http.NotFound(w, r)
			return
		}
		exampleType, link := parts[0], parts[1]
		for _, ex := range examples {
			if ex.Type == exampleType && ex.Link == link {
				tmpl.ExecuteTemplate(w, "example.html", ex)
				return
			}
		}
		http.NotFound(w, r)
	})

	http.Handle("/static/", http.StripPrefix("/static/", http.FileServer(http.Dir("./static"))))

	return http.ListenAndServe(addr, nil)
