func main() {
	// Initialize Jaeger exporter
	je, err := jaeger.NewExporter(jaeger.Options{
		ServiceName: "go-http-server",
		CollectorEndpoint: "http://localhost:14268/api/traces",
	})
	if err != nil {
		log.Fatalf("Failed to create Jaeger exporter: %v", err)
	}
	trace.RegisterExporter(je)
	trace.ApplyConfig(trace.Config{DefaultSampler: trace.AlwaysSample()})

	// Create HTTP client with tracing
	client := &http.Client{
		Transport: &ochttp.Transport{},
	}

	// Define root handler
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		ctx, span := trace.StartSpan(r.Context(), "handleRequest")
		defer span.End()

		span.AddAttributes(
			trace.StringAttribute("method", r.Method),
			trace.StringAttribute("url", r.URL.String()),
		)

		// Make external request with trace context propagation
		req, _ := http.NewRequestWithContext(ctx, "GET", "https://example.com", nil)
		resp, err := client.Do(req)
		if err != nil {
			http.Error(w, fmt.Sprintf("Error making external request: %v", err), http.StatusInternalServerError)
			return
		}
		defer resp.Body.Close()

		span.AddAttributes(trace.Int64Attribute("external.status", int64(resp.StatusCode)))

		// Respond to client
		fmt.Fprintf(w, "hello world")
	})

	// Start HTTP server
	log.Println("Starting server on :50030")
	log.Fatal(http.ListenAndServe(":50030", &ochttp.Handler{}))
