package main

import (
	"github.com/labstack/echo/v4"
	"net/http"
)

type mjHandler struct {
	log                echo.Logger
	tenhouMessageReceiver *tenhou.MessageReceiver
	majsoulMessageQueue  chan []byte
}

func (h *mjHandler) analysisTenhou(c echo.Context) error {
	reqBody := new(bytes.Buffer)
	if err := c.Request().ReadBody(reqBody); err != nil {
		h.log.Error(err)
		return c.String(http.StatusBadRequest, "Failed to read request body")
	}
	go h.tenhouMessageReceiver.Receive(reqBody.Bytes())
	return c.NoContent(http.StatusOK)
}

func (h *mjHandler) analysisMajsoul(c echo.Context) error {
	reqBody := new(bytes.Buffer)
	if err := c.Request().ReadBody(reqBody); err != nil {
		h.log.Error(err)
		return c.String(http.StatusBadRequest, "Failed to read request body")
	}
	h.majsoulMessageQueue <- reqBody.Bytes()
	return c.NoContent(http.StatusOK)
}

func runServer(isHTTPS bool, port int) error {
	e := echo.New()
	handler := &mjHandler{
		log:                e.Logger,
		tenhouMessageReceiver: &tenhou.MessageReceiver{},
		majsoulMessageQueue:  make(chan []byte),
	}

	e.POST("/tenhou", handler.analysisTenhou)
	e.POST("/majsoul", handler.analysisMajsoul)

	if isHTTPS {
		return e.StartTLS(":"+strconv.Itoa(port), "cert.pem", "key.pem")
	} else {
		return e.Start(":" + strconv.Itoa(port))
	}
}
