package main

import (
	"net/http"
	"sync"

	"github.com/labstack/echo/v4"
	"github.com/olahol/melody"
	"golang.org/x/sync/rate"
)

type Config struct {
	// Server settings and parameters
}

type Message struct {
	Content string `json:"content"`
}

type Bridger interface {
	Start()
	Stop()
}

type API struct {
	Messages  *ring.Ring
	RWMutex   sync.RWMutex
	Config    *Config
	mrouter   *melody.Melody
	limiter   *rate.Limiter
}

func New(cfg *Config) bridge.Bridger {
	api := &API{
		Messages:  ring.New(100),
		Config:    cfg,
		mrouter:   melody.New(),
		limiter:   rate.NewLimiter(rate.Every(time.Second), 10),
	}
	e := echo.New()
	e.GET("/health", api.handleHealthcheck)
	e.GET("/messages", api.handleMessages)
	e.GET("/stream", api.handleStream)
	e.POST("/message", api.handlePostMessage)
	e.GET("/ws", api.handleWebsocket)
	go func() {
		if err := e.Start(":8080"); err != nil && err != http.ErrServerClosed {
			panic(err)
		}
	}()
	return api
}

func (a *API) handleWebsocketMessage(message Message, s *melody.Session) {
	a.Messages.Value = message.Content
	a.mrouter.BroadcastJSON(message)
}

func (a *API) handleWebsocket(c echo.Context) error {
	s := new(melody.Session)
	a.mrouter.HandleConnection(s)
	return nil
}

func (a *API) handleHealthcheck(c echo.Context) error {
	return c.String(http.StatusOK, "OK")
}

func (a *API) handleMessages(c echo.Context) error {
	c.JSON(http.StatusOK, map[string]interface{}{
		"messages": a.Messages.Value,
	})
	return nil
}

func (a *API) handleStream(c echo.Context) error {
	return c.Stream(func(w io.Writer) error {
		for {
			select {
			case msg := <-a.Messages.Next():
				_, err := w.Write([]byte(msg.(string)))
				if err != nil {
					return err
				}
			}
		}
	})
}

func (a *API) handlePostMessage(c echo.Context) error {
	var message Message
	if err := c.Bind(&message); err != nil {
		return err
	}
	a.handleWebsocketMessage(message, nil)
	return c.NoContent(http.StatusNoContent)
}
