package main

import (
	"net/http"
	"github.com/labstack/echo/v4"
)

var (
	workflowKey = "your_workflow_key"
	builderKey  = "your_builder_key"
	secretsHit  bool
	triggerHit  bool
)

func startWorkflowServer(host string, port uint64) {
	e := echo.New()

	e.Use(func(next echo.HandlerFunc) echo.HandlerFunc {
		return func(c echo.Context) error {
			req := c.Request()
			if req.Header.Get("X-Workflow-Key") != workflowKey {
				return echo.ErrUnauthorized
			}
			return next(c)
		}
	})

	e.GET("/secrets", func(c echo.Context) error {
		secretsHit = true
		return c.String(http.StatusOK, "Secrets endpoint accessed")
	})

	e.POST("/trigger", func(c echo.Context) error {
		triggerHit = true
		return c.String(http.StatusOK, "Trigger endpoint accessed")
	})

	e.Logger.Fatal(e.Start(host + ":" + httpPort(port)))
}

func httpPort(port uint64) string {
	return fmt.Sprintf("%d", port)
}
