import (
	"net/http"
	"github.com/gin-gonic/gin"
	"log"
	"io/ioutil"
	"net/url"
)

func main() {
	r := gin.Default()

	r.GET("/:path", func(c *gin.Context) {
		path := c.Param("path")
		backendURL := "http://loadbalancer/" + path // Assuming load balancer URL is http://loadbalancer/

		req, err := http.NewRequest("GET", backendURL, nil)
		if err != nil {
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to create request"})
			return
		}

		for key, values := range c.Request.Header {
			for _, value := range values {
				req.Header.Add(key, value)
			}
		}

		client := &http.Client{}
		resp, err := client.Do(req)
		if err != nil {
			log.Println("Proxy error:", err)
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Backend server error"})
			return
		}
		defer resp.Body.Close()

		bodyBytes, err := ioutil.ReadAll(resp.Body)
		if err != nil {
			log.Println("Error reading backend response body:", err)
			c.JSON(http.StatusInternalServerError, gin.H{"error": "Failed to read response body"})
			return
		}

		c.Data(resp.StatusCode, resp.Header.Get("Content-Type"), bodyBytes)
	})

	r.Run(":8080")
}
