	r.Use(gin.Logger())
	r.Use(gin.Recovery())
	r.Use(gzipMiddleware())

	r.GET("/ping", func(c *gin.Context) {
		c.String(http.StatusOK, "pong %d\n", time.Now().Unix())
	})

	r.Run(":8080")
