func ginRun(rps int) {
	limiter := ratelimit.NewBucketWithRate(float64(rps), time.Second)

	r := gin.Default()
	r.Use(func(c *gin.Context) {
		if limiter.TakeAvailable(1) == 0 {
			c.JSON(http.StatusTooManyRequests, gin.H{"error": "Too many requests"})
			c.Abort()
			return
		}
		c.Next()
	})

	r.GET("/rate", func(c *gin.Context) {
		c.JSON(http.StatusOK, gin.H{"message": "Hello, world!", "rate_limit": rps})
	})

	r.Run(":8080")
}

func main() {
	rateLimit := 5 // Example rate limit of 5 requests per second
	ginRun(rateLimit)
