package main

import (
	"encoding/json"
	"fmt"
	"io/ioutil"
	"net/http"
	"time"

	"github.com/yourusername/facebook-sdk-go/model"
)

type TokenInfo struct {
	AccessToken string `json:"access_token"`
}

func GetUserInfo(token TokenInfo) (*model.UserInfo, string, error) {
	url := fmt.Sprintf("https://graph.facebook.com/me?fields=id,name,email,picture&access_token=%s", token.AccessToken)
	client := &http.Client{Timeout: 10 * time.Second}
	req, err := http.NewRequest(http.MethodGet, url, nil)
	if err != nil {
		return nil, "", err
	}

	resp, err := client.Do(req)
	if err != nil {
		return nil, "", err
	}
	defer resp.Body.Close()

	bodyBytes, err := ioutil.ReadAll(resp.Body)
	if err != nil {
		return nil, "", err
	}

	var userInfo model.UserInfo
	err = json.Unmarshal(bodyBytes, &userInfo)
	if err != nil {
		return nil, string(bodyBytes), err
	}

	if resp.StatusCode != http.StatusOK {
		return nil, string(bodyBytes), fmt.Errorf("unexpected status code: %d", resp.StatusCode)
	}

	userInfo.Origin = "Facebook"
	return &userInfo, string(bodyBytes), nil
}
