	if len(req.Address) == 0 {
		return errors.BadRequest("ping.tcp", "missing address")
	}

	c, err := net.Dial("tcp", req.Address)
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}
	defer c.Close()

	if len(req.Data) == 0 {
		rsp.Status = "OK"
		return nil
	}

	fmt.Fprint(c, req.Data)

	data, err := bufio.NewReader(c).ReadString('\n')
	if err != nil {
		rsp.Status = err.Error()
		return nil
	}

	rsp.Status = "OK"
	rsp.Data = data

	return nil
}

