package main

import (
	"context"
	"fmt"
	"net"
	"time"

	pb "path/to/your/proto/package"
)

func Tcp(ctx context.Context, req *pb.TcpRequest, rsp *pb.TcpResponse) error {
	if _, _, err := net.SplitHostPort(req.Address); err != nil {
		return fmt.Errorf("invalid address format: %v", err)
	}

	conn, err := net.DialTimeout("tcp", req.Address, time.Second*5)
	if err != nil {
		rsp.Status = "Connection Error: " + err.Error()
		return nil
	}
	defer conn.Close()

	if req.Data != "" {
		_, err := conn.Write([]byte(req.Data))
		if err != nil {
			rsp.Status = "Send Error: " + err.Error()
			return nil
		}
	}

	buffer := make([]byte, 1024)
	n, err := conn.Read(buffer)
	if err != nil && err != io.EOF {
		rsp.Status = "Receive Error: " + err.Error()
		return nil
	}

	rsp.Status = "OK"
	rsp.Data = string(buffer[:n])
	return nil
}
