type OpenTSDBConfig struct {
	Addr          *net.TCPAddr
	Registry      Registry
	FlushInterval time.Duration
	DurationUnit  time.Duration
	Prefix        string
}

func OpenTSDBWithConfig(c OpenTSDBConfig) {
	ticker := time.NewTicker(c.FlushInterval)
	defer ticker.Stop()
	for {
		select {
		case <-ticker.C:
			if err := c.openTSDB(); err != nil {
				fmt.Println("Error sending metrics:", err)
			}
		}
	}
}

func (c *OpenTSDBConfig) openTSDB() error {
	conn, err := net.DialTCP("tcp", nil, c.Addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	metrics := c.Registry.GetMetrics()
	var buffer bytes.Buffer
	for _, metric := range metrics {
		switch metric.Type {
		case Counter:
			buffer.WriteString(fmt.Sprintf("%s%s counter %f\n", c.Prefix, metric.Name, metric.Value))
		case Gauge:
			buffer.WriteString(fmt.Sprintf("%s%s gauge %f\n", c.Prefix, metric.Name, metric.Value))
		case Histogram:
			buffer.WriteString(fmt.Sprintf("%s%s histogram %f\n", c.Prefix, metric.Name, metric.Value))
		case Meter:
			buffer.WriteString(fmt.Sprintf("%s%s meter %f\n", c.Prefix, metric.Name, metric.Value))
		case Timer:
			buffer.WriteString(fmt.Sprintf("%s%s timer %f\n", c.Prefix, metric.Name, metric.Value.Seconds()))
		}
	}

	_, err = conn.Write(buffer.Bytes())
	return err
}
