	conn, err := c.Dial(a)
	if err != nil {
		return nil, 0, err
	}
	defer conn.Close()

	if err := conn.WriteMsg(m); err != nil {
		return nil, 0, err
	}

	msg, duration, err := conn.ReadMsg()
	if err != nil {
		return nil, 0, err
	}

	return msg, duration, nil
}

func (c *Client) getTimeoutForRequest(timeout time.Duration) time.Duration {
	if timeout == 0 {
		timeout = c.Timeout
	}
	if c.DialTimeout > 0 && timeout < c.DialTimeout {
		timeout = c.DialTimeout
	}
	if c.ReadTimeout > 0 && timeout < c.ReadTimeout {
		timeout = c.ReadTimeout
	}
	if c.WriteTimeout > 0 && timeout < c.WriteTimeout {
		timeout = c.WriteTimeout
	}
	return timeout
}
