package trojan

import (
	"crypto/tls"
	"net"
	"bytes"
	"io"
)

type Trojan struct {
	addr       string
	withTLS    bool
	tlsConfig  *tls.Config
	certFile   string
	keyFile    string
	pass       [56]byte
	fallback   string
	proxy      proxy.Proxy
}

func (t *Trojan) ListenAndServe() {
	listener, err := net.Listen("tcp", t.addr)
	if err != nil {
		panic(err)
	}
	defer listener.Close()

	for {
		conn, err := listener.Accept()
		if err != nil {
			continue
		}
		go t.Serve(conn)
	}
}

func (t *Trojan) Serve(c net.Conn) {
	if t.withTLS {
		tlsConn := tls.Server(c, t.tlsConfig)
		err := tlsConn.Handshake()
		if err != nil {
			return
		}
		c = tlsConn
	}

	cmd, target, err := t.readHeader(c)
	if err != nil {
		return
	}

	switch cmd {
	case socks.CmdConnect:
		t.proxy.Connect(c, target)
	case socks.CmdUDPAssociate:
		t.ServeUoT(c, target)
	default:
		// Handle unknown commands
	}
}

func (t *Trojan) readHeader(r io.Reader) (cmd socks.Cmd, target socks.Addr, err error) {
	buf := make([]byte, 4)
	_, err = io.ReadFull(r, buf)
	if err != nil {
		return
	}

	cmd = socks.Cmd(buf[1])
	targetLen := int(buf[2])

	targetBytes := make([]byte, targetLen)
	_, err = io.ReadFull(r, targetBytes)
	if err != nil {
		return
	}

	target = socks.NewAddr(targetBytes)
	return
}

func (t *Trojan) serveFallback(c net.Conn, fallback string, buf *bytes.Buffer) {
	fallbackConn, err := net.Dial("tcp", fallback)
	if err != nil {
		return
	}
	defer fallbackConn.Close()

	_, err = fallbackConn.Write(buf.Bytes())
	if err != nil {
		return
	}

	go func() {
		_, _ = io.Copy(fallbackConn, c)
	}()
	go func() {
		_, _ = io.Copy(c, fallbackConn)
	}()
}

func (t *Trojan) ServeUoT(c net.Conn, target socks.Addr) {
	udpConn, err := net.ListenPacket("udp", ":0")
	if err != nil {
		return
	}
	defer udpConn.Close()

	go func() {
		buffer := make([]byte, 65535)
		for {
			n, addr, err := udpConn.ReadFrom(buffer)
			if err != nil {
				break
			}
			_, err = c.Write(append([]byte{0x00, 0x00}, buffer[:n]...))
			if err != nil {
				break
			}
		}
	}()

	buffer := make([]byte, 65535)
	for {
		n, err := c.Read(buffer)
		if err != nil {
			break
		}
		_, err = udpConn.WriteTo(buffer[:n], &net.UDPAddr{
			Port: target.Port,
			IP:   net.ParseIP(target.Host),
		})
		if err != nil {
			break
		}
	}
}
