package main

import (
	"net"
	"net/http"
	"strings"
)

type HTTP struct {
	addr     string
	user     string
	password string
	pretend  bool
	proxy    proxy.Proxy
}

func (h *HTTP) ListenAndServe() {
	listener, err := net.Listen("tcp", h.addr)
	if err != nil {
		panic(err)
	}
	defer listener.Close()
	for {
		conn, err := listener.Accept()
		if err != nil {
			continue
		}
		go h.Serve(conn)
	}
}

func (h *HTTP) Serve(conn net.Conn) {
	conn.SetKeepAlive(true)
	req, err := http.ReadRequest(conn)
	if err != nil {
		return
	}
	h.servRequest(req, &proxy.Conn{Conn: conn})
}

func (h *HTTP) servRequest(req *http.Request, pconn *proxy.Conn) {
	auth := req.Header.Get("Authorization")
	if h.user != "" && h.password != "" {
		if !strings.HasPrefix(auth, "Basic ") || base64.StdEncoding.DecodeString(strings.TrimPrefix(auth, "Basic ")) != []byte(h.user+":"+h.password) {
			http.Error(pconn, "Unauthorized", http.StatusUnauthorized)
			return
		}
	}
	if h.pretend && (req.Method == "GET" || req.Method == "HEAD") {
		http.NotFound(pconn, req)
		return
	}
	switch req.ProtoMajor {
	case 1:
		if strings.EqualFold(req.ProtoMinor, "1") && strings.ToUpper(req.Method) == "CONNECT" {
			h.servHTTPS(req, pconn.Conn)
		} else {
			h.servHTTP(req, pconn)
		}
	case 2:
		h.servHTTP(req, pconn)
	default:
		http.Error(pconn, "Unsupported protocol version", http.StatusHTTPVersionNotSupported)
	}
}

func (h *HTTP) servHTTPS(req *http.Request, conn net.Conn) {
	targetHost := req.Host
	tlsConfig := &tls.Config{
		InsecureSkipVerify: true,
		ServerName:         targetHost,
	}
	client := tls.Client(conn, tlsConfig)
	err := client.Handshake()
	if err != nil {
		http.Error(client, "TLS handshake failed", http.StatusInternalServerError)
		return
	}
	pconn := &proxy.Conn{Conn: client}
	h.proxy.Handle(pconn, targetHost, req.URL.Path)
}

func (h *HTTP) servHTTP(req *http.Request, pconn *proxy.Conn) {
	h.proxy.Handle(pconn, req.Host, req.URL.Path)
}
