		s.conns[conn] = struct{}{}
		s.lock.Unlock()

		go s.serveTCPConn(&sync.WaitGroup{}, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer func() {
		s.lock.Lock()
		delete(s.conns, conn)
		s.lock.Unlock()
	}()

	conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
	conn.SetWriteDeadline(time.Now().Add(s.WriteTimeout))

	s.Handler.ServeTCPConn(conn)
}

func (s *Server) ShutdownContext(ctx context.Context) error {
	s.lock.Lock()
	if !s.started {
		s.lock.Unlock()
		return nil
	}
	close(s.shutdown)
	s.lock.Unlock()

	wg := sync.WaitGroup{}
	for conn := range s.conns {
		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
	wg.Wait()

	return nil
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}
