	"github.com/your/api"
)

type unix struct {
	name       string
	config     *Config
	done       chan struct{}
	eventPool  *event.Pool
}

func NewUnixServer(name string, config *Config) (*unix, error) {
	return &unix{
		name:       name,
		config:     config,
		done:       make(chan struct{}),
		eventPool:  event.NewPool(),
	}, nil
}

func (u *unix) ProductLoop(productFunc api.ProductFunc) {
	listener, err := net.Listen("unix", u.config.Path)
	if err != nil {
		panic(err)
	}
	defer listener.Close()
	u.checkBind(u.config.Path)
	u.chmod(u.config.Path, u.config.Mode)

	var activeConnections int32
	for {
		conn, err := listener.Accept()
		if err != nil {
			select {
			case <-u.done:
				return
			default:
				panic(err)
			}
		}

		atomic.AddInt32(&activeConnections, 1)
		go func() {
			u.handleConn(context.Background(), conn, productFunc)
			atomic.AddInt32(&activeConnections, -1)
		}()
		if atomic.LoadInt32(&activeConnections) >= u.config.MaxConnections {
			conn.Close()
		}
	}
}

func (u *unix) handleConn(ctx context.Context, conn net.Conn, productFunc api.ProductFunc) {
	defer conn.Close()

	buffer := make([]byte, u.config.MaxBytes)
	for {
		n, err := conn.Read(buffer)
		if err != nil {
			break
		}

		event := u.eventPool.Get().(*event.Event)
		event.Data = buffer[:n]
		productFunc(event)
		u.eventPool.Put(event)
	}
}

func (u *unix) checkBind(path string) error {
	err := os.Remove(path)
	if err != nil && !os.IsNotExist(err) {
		return err
	}
	return nil
}

func (u *unix) chmod(path, mode string) error {
	return os.Chmod(path, os.FileMode(mode))
}

type Config struct {
	Path         string
	Mode         string
	Timeout      time.Duration
	MaxConnections int32
	MaxBytes     int
}
