type Handler interface {
	Handle(conn net.Conn, msg []byte)
}

type MsgAcceptFunc func(msg []byte) bool

type Server struct {
	Addr            string
	Net             string
	Listener        net.Listener
	TLSConfig       *tls.Config
	PacketConn      net.PacketConn
	Handler         Handler
	ReadTimeout     time.Duration
	WriteTimeout    time.Duration
	IdleTimeout     func() time.Duration
	MsgAcceptFunc   MsgAcceptFunc
	lock            sync.RWMutex
	started         bool
	shutdown        chan struct{}
	conns           map[net.Conn]struct{}
	udpPool         sync.Pool
}

func NewServer(addr, netType string, tlsConfig *tls.Config, handler Handler, readTimeout, writeTimeout time.Duration, idleTimeout func() time.Duration, msgAcceptFunc MsgAcceptFunc) *Server {
	return &Server{
		Addr:            addr,
		Net:             netType,
		TLSConfig:       tlsConfig,
		PacketConn:      nil,
		Handler:         handler,
		ReadTimeout:     readTimeout,
		WriteTimeout:    writeTimeout,
		IdleTimeout:     idleTimeout,
		MsgAcceptFunc:   msgAcceptFunc,
		lock:            sync.RWMutex{},
		started:         false,
		shutdown:        make(chan struct{}),
		conns:           make(map[net.Conn]struct{}),
		udpPool:         sync.Pool{},
	}
}

func (s *Server) ListenAndServe() error {
	var err error
	switch s.Net {
	case "tcp", "tcp4", "tcp6":
		s.Listener, err = net.Listen(s.Net, s.Addr)
	default:
		return errors.New("unsupported network type")
	}
	if s.TLSConfig != nil {
		s.Listener = tls.NewListener(s.Listener, s.TLSConfig)
	}
	s.started = true
	defer s.shutdown <- struct{}{}
	for {
		conn, err := s.Listener.Accept()
		if err != nil {
			if !s.isStarted() {
				return nil
			}
			return err
		}
		go s.serveTCPConn(nil, conn)
	}
}

func (s *Server) serveTCP(listener net.Listener) error {
	var wg sync.WaitGroup
	defer listener.Close()
	for {
		conn, err := listener.Accept()
		if err != nil {
			if !s.isStarted() {
				return nil
			}
			return err
		}
		wg.Add(1)
		go s.serveTCPConn(&wg, conn)
	}
}

func (s *Server) serveTCPConn(wg *sync.WaitGroup, conn net.Conn) {
	defer wg.Done()
	defer conn.Close()
	conn.SetReadDeadline(time.Now().Add(s.ReadTimeout))
	conn.SetWriteDeadline(time.Now().Add(s.WriteTimeout))
	buf := make([]byte, 1024)
	for {
		n, err := conn.Read(buf)
		if err != nil {
			return
		}
		msg := buf[:n]
		if !s.MsgAcceptFunc(msg) {
			continue
		}
		s.Handler.Handle(conn, msg)
	}
}

func (s *Server) isStarted() bool {
	s.lock.RLock()
	defer s.lock.RUnlock()
	return s.started
}

func (s *Server) init() {
	s.conns = make(map[net.Conn]struct{})
}

func (s *Server) getReadTimeout() time.Duration {
	return s.ReadTimeout
}
