	stream, err := client.Download(ctx, downloadRequest)
	if err != nil {
		return nil, fmt.Errorf("failed to start download: %w", err)
	}

	results := make(map[string]*dfdaemonv1.DownResult)
	for {
		result, err := stream.Recv()
		if err != nil {
			if err == io.EOF {
				break
			}
			return nil, fmt.Errorf("error receiving download result: %w", err)
		}
		results[result.Output] = result
	}

	return results, nil
}
