```go
package main

import (
	"context"
	"crypto/tls"
	"flag"
	"log"
	"time"

	pb "path/to/routeguidepb"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
)

func main() {
	var (
		tls            bool
		serverHostOverride string
		caFile         string
		serverAddr     string
	)

	flag.BoolVar(&tls, "tls", false, "Use TLS encryption")
	flag.StringVar(&serverHostOverride, "server_host_override", "", "Server name for certificate validation")
	flag.StringVar(&caFile, "ca_file", "", "Path to CA certificate file")
	flag.StringVar(&serverAddr, "server_addr", "localhost:50051", "Server address in format host:port")
	flag.Parse()

	var opts []grpc.DialOption
	if tls {
		certPool := x509.NewCertPool()
		caBytes, err := ioutil.ReadFile(caFile)
		if err != nil {
			log.Fatalf("Failed to read CA file: %v", err)
		}
		if !certPool.AppendCertsFromPEM(caBytes) {
			log.Fatalf("Failed to append PEM from CA file")
		}

		creds := credentials.NewClientTLSFromCert(certPool, serverHostOverride)
		opts = append(opts, grpc.WithTransportCredentials(creds))
	} else {
		opts = append(opts, grpc.WithInsecure())
	}

	conn, err := grpc.Dial(serverAddr, opts...)
	if err != nil {
		log.Fatalf("did not connect: %v", err)
	}
	defer conn.Close()
	client := pb.NewRouteGuideClient(conn)

	point := &pb.Point{Latitude: 409146138, Longitude: -746194936}
	printFeature(client, point)

	rectangle := &pb.Rectangle{
		SouthWest: &pb.Point{Latitude: 409146138, Longitude: -746194936},
		NorthEast: &pb.Point{Latitude: 409146138, Longitude: -746194936},
	}
	printFeatures(client, rectangle)

	runRecordRoute(client)

	runRouteChat(client)
}

func printFeature(client pb.RouteGuideClient, point *pb.Point) {
	feature, err := client.GetFeature(context.Background(), point)
	if err != nil {
		log.Fatalf("failed to get feature: %v", err)
	}
	fmt.Printf("Feature found: %v\n", feature)
}

func printFeatures(client pb.RouteGuideClient, rectangle *pb.Rectangle) {
	stream, err := client.ListFeatures(context.Background(), rectangle)
	if err != nil {
		log.Fatalf("failed to list features: %v", err)
	}
	for {
		feature, err := stream.Recv()
		if err == io.EOF {
			break
		}
		if err != nil {
			log.Fatalf("failed to receive feature: %v", err)
		}
		fmt.Printf("Feature found: %v\n", feature)
	}
}

func runRecordRoute(client pb.RouteGuideClient) {
	points := []*pb.Point{
		&pb.Point{Latitude: 409146138, Longitude: -746194936},
		&pb.Point{Latitude: 409146138, Longitude: -746194936},
		// Add more points as needed
	}

	stream, err := client.RecordRoute(context.Background())
	if err != nil {
		log.Fatalf("failed to record route: %v", err)
	}

	for _, point := range points {
		if err := stream.Send(point); err != nil {
			log.Fatalf("failed to send point: %v", err)
		}
	}

	status, err := stream.CloseAndRecv()
	if err != nil {
		log.Fatalf("failed to close and receive status: %v", err)
	}
	fmt.Printf("Route summary: %v\n", status)
}

func runRouteChat(client pb.RouteGuideClient) {
	stream, err := client.RouteChat(context.Background())
	if err != nil {
		log.Fatalf("failed to start route chat: %v", err)
	}

	go func() {
		for {
			response, err := stream.Recv()
			if err == io.EOF {
				return
			}
			if err != nil {
				log.Fatalf("failed to receive response: %v", err)
			}
			fmt.Printf("Received message: %v\n", response)
