)

func setupPubSubClient() {
	ctx := context.Background()
	conn, err := grpc.Dial(pstest.PubSubAddr(), grpc.WithInsecure())
	if err != nil {
		log.Fatalf("Failed to connect to PSTest server: %v", err)
	}
	defer conn.Close()

	client := pb.NewPublisherClient(conn)
	topic := &pb.Topic{
		Name: "projects/my-project/topics/my-topic",
	}
	createTopicReq := &pb.CreateTopicRequest{
		Parent: "projects/my-project",
		Topic:  topic,
	}

	createdTopic, err := client.CreateTopic(ctx, createTopicReq)
	if err != nil {
		log.Fatalf("Failed to create topic: %v", err)
	}
	log.Printf("Created topic: %s", createdTopic.Name)

	subscription := &pb.Subscription{
		Name:    "projects/my-project/subscriptions/my-subscription",
		Topic:   createdTopic.Name,
		AckDeadlineSeconds: 60,
	}
	createSubscriptionReq := &pb.CreateSubscriptionRequest{
		Parent:       "projects/my-project",
		Subscription: subscription,
	}

	createdSubscription, err := client.CreateSubscription(ctx, createSubscriptionReq)
	if err != nil {
		log.Fatalf("Failed to create subscription: %v", err)
	}
	log.Printf("Created subscription: %s", createdSubscription.Name)

	time.Sleep(2 * time.Second)

	deleteSubscriptionReq := &pb.DeleteSubscriptionRequest{
		Subscription: createdSubscription.Name,
	}
	err = client.DeleteSubscription(ctx, deleteSubscriptionReq)
	if err != nil {
		log.Fatalf("Failed to delete subscription: %v", err)
	}
	log.Println("Deleted subscription")

	deleteTopicReq := &pb.DeleteTopicRequest{
		Topic: createdTopic.Name,
	}
	err = client.DeleteTopic(ctx, deleteTopicReq)
	if err != nil {
		log.Fatalf("Failed to delete topic: %v", err)
	}
	log.Println("Deleted topic")
}

func main() {
	setupPubSubClient()
}
