package http_probe

import (
	"bytes"
	"net/http"
	"time"

	"github.com/yourcompany/types" // Adjust import based on your project structure
	"go.uber.org/zap"
)

func PerformHTTPProbe(protocol, address, path string, port int, headerList []*types.HTTPHeader, timeout time.Duration, responseSuccessFlag string, log *zap.SugaredLogger) (string, error) {
	url := fmt.Sprintf("%s://%s:%d%s", protocol, address, port, path)
	client := &http.Client{Timeout: timeout}
	req, err := http.NewRequest("GET", url, nil)
	if err != nil {
		log.Errorf("Failed to create HTTP request: %v", err)
		return "Failure", err
	}

	for _, header := range headerList {
		req.Header.Add(header.Key, header.Value)
	}

	resp, err := client.Do(req)
	if err != nil {
		log.Errorf("HTTP request failed: %v", err)
		return "Failure", err
	}
	defer resp.Body.Close()

	if resp.StatusCode != http.StatusOK {
		log.Errorf("Unexpected HTTP status code: %d", resp.StatusCode)
		return "Failure", fmt.Errorf("unexpected HTTP status code: %d", resp.StatusCode)
	}

	if responseSuccessFlag != "" {
		bodyBytes, _ := ioutil.ReadAll(resp.Body)
		bodyText := string(bodyBytes)
		if !strings.Contains(bodyText, responseSuccessFlag) {
			log.Errorf("Response does not contain success flag: %s", responseSuccessFlag)
			return "Failure", fmt.Errorf("response does not contain success flag: %s", responseSuccessFlag)
		}
	}

	log.Infof("HTTP probe successful")
	return "Success", nil
}
