package main

import (
	"context"
	"net"

	"google.golang.org/grpc"
	"google.golang.org/grpc/metadata"
	"sync"

	collpb "path/to/otel/collection/proto"
)

type GRPCCollector struct {
	collpb.UnimplementedMetricsServiceServer
	headersMu sync.Mutex
	headers   metadata.MD
	storage   *Storage
	resultCh  <-chan ExportResult
	listener  net.Listener
	srv       *grpc.Server
}

func NewGRPCCollector(endpoint string, resultCh <-chan ExportResult) (*GRPCCollector, error) {
	lis, err := net.Listen("tcp", endpoint)
	if err != nil {
		return nil, err
	}
	s := grpc.NewServer()
	c := &GRPCCollector{
		listener: lis,
		srv:      s,
		resultCh: resultCh,
	}
	collpb.RegisterMetricsServiceServer(s, c)
	go func() {
		if err := s.Serve(lis); err != nil && err != grpc.ErrServerStopped {
			panic(err)
		}
	}()
	return c, nil
}

func (c *GRPCCollector) Shutdown() {
	c.srv.GracefulStop()
}

func (c *GRPCCollector) Addr() net.Addr {
	return c.listener.Addr()
}

func (c *GRPCCollector) Collect() *Storage {
	return c.storage
}

func (c *GRPCCollector) Headers() map[string][]string {
	c.headersMu.Lock()
	defer c.headersMu.Unlock()
	return c.headers.GetMap()
}

func (c *GRPCCollector) Export(ctx context.Context, req *collpb.ExportMetricsServiceRequest) (*collpb.ExportMetricsServiceResponse, error) {
	md, ok := metadata.FromIncomingContext(ctx)
	if !ok {
		return nil, status.Errorf(codes.InvalidArgument, "missing metadata")
	}
	c.headersMu.Lock()
	c.headers = md.Copy()
	c.headersMu.Unlock()

	// Process metrics here
	c.storage.Store(req.Metrics)

	select {
	case res := <-c.resultCh:
		return &collpb.ExportMetricsServiceResponse{ExportResult: res}, nil
	case <-ctx.Done():
		return nil, ctx.Err()
	}
}
