package main

import (
	"context"
	"errors"
	"net"
	"sync"

	v1alpha1 "github.com/container-storage-interface/spec/lib/go/csi/v1alpha1"
	"google.golang.org/grpc"
)

type MockCSIProviderServer struct {
	grpcServer *grpc.Server
	listener   net.Listener
	socketPath string
	returnErr  error
	errorCode  string
	objects    []*v1alpha1.ObjectVersion
	files      []*v1alpha1.File
	mu         sync.Mutex
}

func NewMockCSIProviderServer(socketPath string) (*MockCSIProviderServer, error) {
	server := &MockCSIProviderServer{
		socketPath: socketPath,
	}
	return server, nil
}

func (s *MockCSIProviderServer) SetReturnError(err error) {
	s.returnErr = err
}

func (s *MockCSIProviderServer) SetObjects(objects map[string]string) {
	for key, value := range objects {
		s.objects = append(s.objects, &v1alpha1.ObjectVersion{
			Object: &v1alpha1.Object{
				Name: key,
			},
			Version: value,
		})
	}
}

func (s *MockCSIProviderServer) SetFiles(files []*v1alpha1.File) {
	s.files = files
}

func (s *MockCSIProviderServer) SetProviderErrorCode(errorCode string) {
	s.errorCode = errorCode
}

func (s *MockCSIProviderServer) Start() error {
	var err error
	s.listener, err = net.Listen("unix", s.socketPath)
	if err != nil {
		return err
	}
	s.grpcServer = grpc.NewServer()
	v1alpha1.RegisterControllerServiceServer(s.grpcServer, s)
	v1alpha1.RegisterNodeServiceServer(s.grpcServer, s)
	go func() {
		err := s.grpcServer.Serve(s.listener)
		if err != nil && !errors.Is(err, grpc.ErrServerStopped) {
			panic(err)
		}
	}()
	return nil
}

func (s *MockCSIProviderServer) Stop() {
	s.grpcServer.GracefulStop()
	s.listener.Close()
}

func (s *MockCSIProviderServer) Mount(ctx context.Context, req *v1alpha1.MountRequest) (*v1alpha1.MountResponse, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	if s.returnErr != nil {
		return nil, s.returnErr
	}
	return &v1alpha1.MountResponse{}, nil
}

func (s *MockCSIProviderServer) Version(ctx context.Context, req *v1alpha1.VersionRequest) (*v1alpha1.VersionResponse, error) {
	s.mu.Lock()
	defer s.mu.Unlock()
	if s.returnErr != nil {
		return nil, s.returnErr
	}
	return &v1alpha1.VersionResponse{
		Version: &v1alpha1.Version{
			Major: 0,
			Minor: 1,
			Patch: 0,
		},
	}, nil
