package csi

import (
	"context"
	"net"
	"sync"

	"google.golang.org/grpc"
)

type CSIDriver struct {
	listener  net.Listener
	server    *grpc.Server
	servers   *CSIDriverServers
	wg        sync.WaitGroup
	running   bool
	lock      sync.Mutex
	creds     *CSICreds
	logGRPC   LogGRPC
}

func NewCSIDriver(servers *CSIDriverServers) *CSIDriver {
	return &CSIDriver{
		servers: servers,
	}
}

func (d *CSIDriver) Start(listener net.Listener, interceptor grpc.UnaryServerInterceptor) error {
	d.lock.Lock()
	if d.running {
		d.lock.Unlock()
		return ErrAlreadyRunning
	}
	d.listener = listener
	d.server = grpc.NewServer(grpc.UnaryInterceptor(interceptor))
	d.servers.Register(d.server)
	d.running = true
	d.lock.Unlock()

	go d.goServe(make(chan<- bool))

	return nil
}

func (d *CSIDriver) goServe(stopChan chan<- bool) {
	defer func() {
		d.wg.Done()
	}()

	for {
		conn, err := d.listener.Accept()
		if err != nil {
			if d.running {
				d.logGRPC("Error accepting connection", err)
			} else {
				close(stopChan)
			}
			return
		}
		d.wg.Add(1)
		go d.serveConn(conn)
	}
}

func (d *CSIDriver) Stop() {
	d.lock.Lock()
	if !d.running {
		d.lock.Unlock()
		return
	}
	d.running = false
	d.lock.Unlock()

	d.server.GracefulStop()
	d.wg.Wait()
	d.listener.Close()
}

func (d *CSIDriver) callInterceptor(ctx context.Context, req interface{}, info *grpc.UnaryServerInfo, handler grpc.UnaryHandler) (interface{}, error) {
	if d.creds != nil {
		err := d.creds.Authenticate(ctx, info.FullMethod)
		if err != nil {
			return nil, err
		}
	}
	resp, err := handler(ctx, req)
	if resp != nil {
		d.logGRPC("Request processed successfully", nil)
	} else if err != nil {
		d.logGRPC("Error processing request", err)
	}
	return resp, err
}
