```go
package grpcserver

import (
	"context"
	"crypto/tls"
	"net"
	"time"

	"google.golang.org/grpc"
	"google.golang.org/grpc/credentials"
	"github.com/rs/zerolog"
)

type GRPCServerConfig struct {
	Address        string        `flag:"address"`
	Network        string        `flag:"network"`
	TLSCertPath    string        `flag:"tls-cert-path"`
	TLSKeyPath     string        `flag:"tls-key-path"`
	MaxConnAge     time.Duration `flag:"max-conn-age"`
	Enabled        bool          `flag:"enabled"`
	BufferSize     int           `flag:"buffer-size"`
	ClientCAPath   string        `flag:"client-ca-path"`
	MaxWorkers     uint32        `flag:"max-workers"`
	flagPrefix     string
}

func (c *GRPCServerConfig) Complete(level zerolog.Level, svcRegistrationFn func(*grpc.Server), opts ...grpc.ServerOption) (RunnableGRPCServer, error) {
	listener, dialer, netDialer, err := c.listenerAndDialer()
	if err != nil {
		return nil, err
	}
	tlsOpts, certWatcher, err := c.tlsOpts()
	if err != nil {
		return nil, err
	}
	clientCreds, err := c.clientCreds()
	if err != nil {
		return nil, err
	}

	runnable := &completedGRPCServer{
		opts:            append(tlsOpts, opts...),
		listener:        listener,
		svcRegistrationFn: svcRegistrationFn,
		prestopFunc:       func() {},
		stopFunc:        func() { listener.Close() },
		dial:            func(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) { return grpc.DialContext(ctx, listener.Addr().String(), append(opts, grpc.WithTransportCredentials(clientCreds))...) },
		netDial:         netDialer,
		creds:           clientCreds,
		certWatcher:     certWatcher,
	}

	return runnable, nil
}

func (c *GRPCServerConfig) listenerAndDialer() (net.Listener, DialFunc, NetDialFunc, error) {
	var listener net.Listener
	var err error
	switch c.Network {
	case "tcp":
		listener, err = net.Listen("tcp", c.Address)
	case "unix":
		listener, err = net.Listen("unix", c.Address)
	case "buffered":
		listener, err = net.ListenBuffered(c.BufferSize, c.Address)
	default:
		return nil, nil, nil, fmt.Errorf("unsupported network type: %s", c.Network)
	}
	if err != nil {
		return nil, nil, nil, err
	}

	dialer := func(ctx context.Context, addr string) (*grpc.ClientConn, error) {
		return grpc.DialContext(ctx, addr, grpc.WithInsecure())
	}

	netDialer := func(ctx context.Context, addr string) (net.Conn, error) {
		return net.DialTimeout("tcp", addr, 5*time.Second)
	}

	return listener, dialer, netDialer, nil
}

func (c *GRPCServerConfig) tlsOpts() ([]grpc.ServerOption, *certwatcher.CertWatcher, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return nil, nil, nil
	}

	serverCert, err := tls.LoadX509KeyPair(c.TLSCertPath, c.TLSKeyPath)
	if err != nil {
		return nil, nil, err
	}

	tlsConfig := &tls.Config{Certificates: []tls.Certificate{serverCert}}
	if c.ClientCAPath != "" {
		clientCertPool := x509.NewCertPool()
		data, err := ioutil.ReadFile(c.ClientCAPath)
		if err != nil {
			return nil, nil, err
		}
		if !clientCertPool.AppendCertsFromPEM(data) {
			return nil, nil, errors.New("failed to append client certs")
		}
		tlsConfig.ClientAuth = tls.RequireAndVerifyClientCert
		tlsConfig.ClientCAs = clientCertPool
	}

	certWatcher, err := certwatcher.New(certwatcher.Options{
		CertFile: c.TLSCertPath,
		KeyFile:  c.TLSKeyPath,
	})
	if err != nil {
		return nil, nil, err
	}

	return []grpc.ServerOption{grpc.Creds(credentials.NewTLS(tlsConfig))}, certWatcher, nil
}

func (c *GRPCServerConfig) clientCreds() (credentials.TransportCredentials, error) {
	if c.TLSCertPath == "" || c.TLSKeyPath == "" {
		return credentials.NewInsecure(), nil
	}

	return credentials.NewTLS(&tls.Config{}), nil
}

type RunnableGRPCServer interface {
	WithOpts(...grpc.ServerOption) RunnableGRPCServer
	Listen(context.Context) func() error
	DialContext(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	NetDialContext(context.Context, string) (net.Conn, error)
	Insecure() bool
	GracefulStop()
}

type completedGRPCServer struct {
	opts            []grpc.ServerOption
	listener        net.Listener
	svcRegistrationFn func(*grpc.Server)
	prestopFunc       func()
	stopFunc        func()
	dial            func(context.Context, ...grpc.DialOption) (*grpc.ClientConn, error)
	netDial         func(context.Context, string) (net.Conn, error)
	creds           credentials.TransportCredentials
	certWatcher     *certwatcher.CertWatcher
}

func (s *completedGRPCServer) WithOpts(opts ...grpc.ServerOption) RunnableGRPCServer {
	s.opts = append(s.opts, opts...)
	return s
}

func (s *completedGRPCServer) Listen(ctx context.Context) func() error {
	return func() error {
		server := grpc.NewServer(s.opts...)
		s.svcRegistrationFn(server)
		go func() {
			defer s.stopFunc()
			err := server.Serve(s.listener)
			if err != nil && err != io.EOF {
				log.Error().Err(err).Msg("gRPC server error")
			}
		}()
		select {
		case <-ctx.Done():
			s.prestopFunc()
			server.GracefulStop()
			return ctx.Err()
		}
	}
}

func (s *completedGRPCServer) DialContext(ctx context.Context, opts ...grpc.DialOption) (*grpc.ClientConn, error) {
	return s.dial(ctx, opts...)
}

func (s *completedGRPCServer) NetDialContext(ctx context.Context, addr string) (net.Conn, error) {
	return s.netDial(ctx, addr)
}

func (s *completedGRPCServer) Insecure() bool {
	return false
}

func (s *completedGRPCServer) GracefulStop() {
	s.certWatcher.Stop()
	s.stopFunc()
}
