	if mtr.exportError != nil {
		return nil, mtr.exportError
	}
	mtr.mux.Lock()
	defer mtr.mux.Unlock()
	mtr.requestCount++
	mtr.totalItems += int32(req.ResourceSpans().Len())
	if mtr.hasMetadata {
