package main

import (
	"context"
	"log"
	"net"
	"time"

	pb "path/to/your/echo/proto"
	"google.golang.org/grpc"
	"google.golang.org/grpc/health"
	healthpb "google.golang.org/grpc/health/grpc_health_v1"
)

var (
	port   int
	sleep  time.Duration
	server *grpc.Server
	healthSrv *health.Server
)

type echoServer struct{}

func (s *echoServer) Echo(ctx context.Context, req *pb.EchoRequest) (*pb.EchoResponse, error) {
	return &pb.EchoResponse{Message: req.Message}, nil
}

func toggleHealth() {
	for {
		if healthSrv.GetServingStatus("service") == healthpb.HealthCheckResponse_SERVING {
			healthSrv.SetServingStatus("service", healthpb.HealthCheckResponse_NOT_SERVING)
		} else {
			healthSrv.SetServingStatus("service", healthpb.HealthCheckResponse_SERVING)
		}
		time.Sleep(sleep)
	}
}

func main() {
	lis, err := net.Listen("tcp", fmt.Sprintf(":%d", port))
	if err != nil {
		log.Fatalf("failed to listen: %v", err)
	}

	healthSrv = health.NewServer()
	healthSrv.SetServingStatus("service", healthpb.HealthCheckResponse_SERVING)
	go toggleHealth()

	server = grpc.NewServer(grpc.Insecure(), grpc.StatsHandler(healthSrv))
	pb.RegisterEchoServer(server, &echoServer{})

	log.Printf("server listening at %v", lis.Addr())
	if err := server.Serve(lis); err != nil {
		log.Fatalf("failed to serve: %v", err)
	}
}
