	if err != nil {
		return err
	}
	return nil
}

func (c *RocketMQClient) sendOneWayMessage(ctx context.Context, topic string, body []byte) error {
	msg := primitive.Message{
		Topic: topic,
		Body:  body,
	}
	err := c.client.SendOneway(ctx, msg)
	return err
}

func (c *RocketMQClient) sendSyncMessage(ctx context.Context, topic string, body []byte) (*primitive.SendResult, error) {
	msg := primitive.Message{
		Topic: topic,
		Body:  body,
	}
	result, err := c.client.SendSync(ctx, msg)
	return result, err
}

func (c *RocketMQClient) sendAsyncMessage(ctx context.Context, topic string, body []byte, callback func(ctx context.Context, result *primitive.SendResult, err error)) error {
	msg := primitive.Message{
		Topic: topic,
		Body:  body,
	}
	err := c.client.SendAsync(ctx, msg, func(ctx context.Context, result *primitive.SendResult, err error) {
		callback(ctx, result, err)
	})
	return err
}

func (c *RocketMQClient) sendDelaySyncMessage(ctx context.Context, topic string, body []byte, tag string, keys []string, delayLevel int) (*primitive.SendResult, error) {
	msg := primitive.Message{
		Topic: topic,
		Body:  body,
		Tag:   tag,
		Keys:  keys,
		DelayTimeLevel: delayLevel,
	}
	result, err := c.client.SendSync(ctx, msg)
	return result, err
}

