	if !p.started {
		return nil, fmt.Errorf("producer not started")
	}
	var m *primitive.Message
	if msg == nil {
		m = &primitive.Message{
			Topic: p.name,
			Body:  msgBytes,
		}
		if len(tag) > 0 {
			m.Tags = tag[0]
		}
	} else {
		m = msg
	}
	result, err = p.Producer.SendSync(ctx, m)
	return
}
