func googlefont(f string) []string {
	url := fmt.Sprintf("https://fonts.googleapis.com/css?family=%s&display=swap", f)
	resp, err := http.Get(url)
	if err != nil || resp.StatusCode != 200 {
		return []string{}
	}
	defer resp.Body.Close()
	body, _ := ioutil.ReadAll(resp.Body)
	css := string(body)
	fonts := extractFontVariants(css)
	return fonts
}

func extractFontVariants(css string) []string {
	variants := []string{}
	lines := strings.Split(css, "\n")
	for _, line := range lines {
		if strings.Contains(line, "@font-face") {
			parts := strings.Split(line, "font-family:")
			if len(parts) > 1 {
				family := strings.TrimSpace(strings.Trim(parts[1], "\""))
				variantPart := strings.Split(family, ":")[1]
				variants = append(variants, variantPart)
			}
		}
	}
	return variants
}

func main() {
	fonts := googlefont("Roboto")
	fmt.Println(fonts)
}
