import (
	"bytes"
	"compress/gzip"
	"io/ioutil"
	"net/http"
	"strings"

	"golang.org/x/text/encoding/simplifiedchinese"
	"golang.org/x/text/transform"
)

type Properties struct {
	Data map[string]interface{}
}

func LoadURL(url string, ignoreMissing bool) (*Properties, error) {
	resp, err := http.Get(url)
	if err != nil {
		return nil, err
	}
	defer resp.Body.Close()

	if resp.StatusCode == http.StatusNotFound && ignoreMissing {
		return &Properties{Data: make(map[string]interface{})}, nil
	}

	if resp.StatusCode != http.StatusOK {
		return nil, fmt.Errorf("HTTP error: %s", resp.Status)
	}

	contentEncoding := resp.Header.Get("Content-Encoding")
	var reader io.Reader = resp.Body

	switch contentEncoding {
	case "gzip":
		reader, err = gzip.NewReader(reader)
		if err != nil {
			return nil, err
		}
	case "gzip, deflate":
		reader, err = gzip.NewReader(reader)
		if err != nil {
			return nil, err
		}
	case "deflate":
		reader, err = zlib.NewReader(reader)
		if err != nil {
			return nil, err
		}
	default:
		// No compression
	}

	contentType := resp.Header.Get("Content-Type")
	if strings.Contains(contentType, "charset=utf-8") {
		reader = transform.NewReader(reader, simplifiedchinese.GBK.NewDecoder())
	} else if strings.Contains(contentType, "charset=gbk") {
		reader = transform.NewReader(reader, simplifiedchinese.GBK.NewDecoder())
	}

	body, err := ioutil.ReadAll(reader)
	if err != nil {
		return nil, err
	}

	// Assuming JSON for simplicity
	props := &Properties{}
	err = json.Unmarshal(body, props)
	if err != nil {
		return nil, err
	}

	return props, nil
}
