package rabbitmq

import (
	"context"
	"sync"
	"time"

	"github.com/streadway/amqp"
)

type Endpoint struct {
	URI      string
	Exchange string
	Queue    string
}

type AMQPConn struct {
	mu     sync.Mutex
	ep     Endpoint
	conn   *amqp.Connection
	channel *amqp.Channel
	ex     bool
	t      time.Time
}

func NewAMQPConn(ep Endpoint) (*AMQPConn, error) {
	conn, err := amqp.Dial(ep.URI)
	if err != nil {
		return nil, err
	}
	ch, err := conn.Channel()
	if err != nil {
		return nil, err
	}
	return &AMQPConn{
		ep:     ep,
		conn:   conn,
		channel: ch,
		t:      time.Now(),
	}, nil
}

func (ac *AMQPConn) Send(msg string) error {
	ac.mu.Lock()
	defer ac.mu.Unlock()

	if time.Since(ac.t) > 10*time.Second || ac.ex {
		if err := ac.reconnect(); err != nil {
			return err
		}
	}

	if _, err := ac.channel.QueueDeclare(
		ac.ep.Queue, // name
		true,        // durable
		false,       // delete when unused
		false,       // exclusive
		false,       // no-wait
		nil,         // arguments
	); err != nil {
		return err
	}

	if err := ac.channel.ExchangeDeclare(
		ac.ep.Exchange, // name
		"direct",       // type
		true,           // durable
		false,          // auto-delete
		false,          // internal
		false,          // no-wait
		nil,            // arguments
	); err != nil {
		return err
	}

	if err := ac.channel.Publish(
		ac.ep.Exchange, // exchange
		ac.ep.Queue,    // routing key
		false,          // mandatory
		false,          // immediate
		amqp.Publishing{
			ContentType: "text/plain",
			Body:        []byte(msg),
		},
	); err != nil {
		return err
	}

	ac.t = time.Now()
	return nil
}

func (ac *AMQPConn) reconnect() error {
	if err := ac.conn.Close(); err != nil {
		return err
	}
	newConn, err := amqp.Dial(ac.ep.URI)
	if err != nil {
		return err
	}
	newChannel, err := newConn.Channel()
	if err != nil {
		return err
	}
	ac.conn = newConn
	ac.channel = newChannel
	ac.ex = false
	return nil
}
