package websocketproxy

import (
	"net/http"
	"net/url"
	"github.com/gorilla/websocket"
)

type WebsocketProxy struct {
	Director func(incoming *http.Request, out http.Header)
	Backend  func(*http.Request) *url.URL
	Upgrader *websocket.Upgrader
	Dialer   *websocket.Dialer
}

func (p *WebsocketProxy) ServeHTTP(rw http.ResponseWriter, req *http.Request) {
	if p.Director != nil {
		p.Director(req, rw.Header())
	}

	clientConn, err := p.Upgrader.Upgrade(rw, req, nil)
	if err != nil {
		http.Error(rw, "Could not set websocket upgrade: "+err.Error(), http.StatusInternalServerError)
		return
	}
	defer clientConn.Close()

	backendURL := p.Backend(req)
	backendConn, _, err := p.Dialer.Dial(backendURL.String(), nil)
	if err != nil {
		http.Error(rw, "Could not connect to backend: "+err.Error(), http.StatusInternalServerError)
		return
	}
	defer backendConn.Close()

	done := make(chan error, 2)
	go func() {
		done <- p.forwardMessages(clientConn, backendConn)
	}()
	go func() {
		done <- p.forwardMessages(backendConn, clientConn)
	}()

	<-done
}

func (p *WebsocketProxy) forwardMessages(src, dst *websocket.Conn) error {
	for {
		messageType, message, err := src.ReadMessage()
		if err != nil {
			return err
		}
		err = dst.WriteMessage(messageType, message)
		if err != nil {
			return err
		}
	}
}
