package main

import (
	"encoding/binary"
	"errors"
	"net"
	"strings"
)

func WakeUp(macAddr, broadcastIP string, bcastInterface string, port int) error {
	if !strings.Contains(macAddr, ":") || len(macAddr) != 17 {
		return errors.New("Invalid MAC address format")
	}

	macBytes := make([]byte, 6)
	for i := 0; i < 6; i++ {
		hexVal, err := strconv.ParseInt(macAddr[3*i+0:3*i+2], 16, 8)
		if err != nil {
			return err
		}
		macBytes[i] = byte(hexVal)
	}

	payload := bytes.Repeat(macBytes[:], 16)
	payload = append(payload, macBytes...)

	addr, err := net.ResolveUDPAddr("udp", fmt.Sprintf("%s:%d", broadcastIP, port))
	if err != nil {
		return err
	}

	conn, err := net.DialUDP("udp", &net.UDPAddr{IP: net.ParseIP(bcastInterface)}, addr)
	if err != nil {
		return err
	}
	defer conn.Close()

	_, err = conn.Write(payload)
	return err
}
