package dnsresolver

import (
	"context"
	"net"
	"sync"
	"time"

	"go.uber.org/zap"
	"golang.org/x/net/dns/dnsmessage"
)

type Bootstrap struct {
	fqdn       string
	port       uint16
	bootstrap  *net.UDPAddr
	qt         uint16
	logger     *zap.Logger
	updating   atomic.Bool
	nextUpdate time.Time
	readyNotify chan struct{}
	m          sync.Mutex
	ready      bool
	addrStr    string
}

func NewBootstrap(fqdn string, port uint16, bootstrap string, qt uint16, logger *zap.Logger) (*Bootstrap, error) {
	resolver := &Bootstrap{
		fqdn:       fqdn,
		port:       port,
		bootstrap:  &net.UDPAddr{IP: net.ParseIP(bootstrap), Port: int(port)},
		qt:         qt,
		logger:     logger,
		readyNotify: make(chan struct{}),
	}
	return resolver, nil
}

func (b *Bootstrap) resolve(ctx context.Context, qt uint16) (netip.Addr, uint32, error) {
	var addr netip.Addr
	var ttl uint32
	var err error

	for {
		select {
		case <-ctx.Done():
			return addr, ttl, ctx.Err()
		default:
			if b.updating.Load() {
				b.logger.Debug("Waiting for previous update to complete")
				time.Sleep(100 * time.Millisecond)
				continue
			}

			b.updating.Store(true)
			defer b.updating.Store(false)

			err = b.queryDNS(qt)
			if err == nil {
				addr = netip.MustParse(addrStr)
				ttl = response.Header.TTL
				close(b.readyNotify)
				b.ready = true
				return addr, ttl, nil
			} else {
				b.logger.Error("DNS query failed", zap.Error(err))
				time.Sleep(time.Second)
			}
		}
	}
}

func (b *Bootstrap) queryDNS(qt uint16) error {
	conn, err := net.DialUDP("udp", nil, b.bootstrap)
	if err != nil {
		return err
	}
	defer conn.Close()

	query, err := buildQuery(b.fqdn, qt)
	if err != nil {
		return err
	}

	if _, err := conn.Write(query); err != nil {
		return err
	}

	buffer := make([]byte, 4096)
	n, err := conn.Read(buffer)
	if err != nil {
		return err
	}

	response, err := parseResponse(buffer[:n])
	if err != nil {
		return err
	}

	if len(response.Answers) > 0 {
		addrStr = response.Answers[0].String()
		return nil
	}

	return errors.New("no answer found")
}

func buildQuery(fqdn string, qt uint16) ([]byte, error) {
	msg := new(dnsmessage.Message)
	msg.Header.Id = dnsmessage.Id(0)
	msg.Header.Response = false
	msg.Header.OpCode = dnsmessage.Query
	msg.Header.AuthoritativeAnswer = false
	msg.Header.Truncated = false
	msg.Header.RecursionDesired = true
	msg.Header.RecursionAvailable = false
	msg.Header.Zero = false
	msg.Header.AuthenticData = false
	msg.Header.CheckingDisabled = false
	msg.Header.Rcode = dnsmessage.NoError

	q := new(dnsmessage.Question)
	q.Name = dnsmessage.Fqdn(fqdn)
	q.Type = dnsmessage.Type(qt)
	q.Class = dnsmessage.ClassINET

	msg.Questions = []dnsmessage.Question{*q}

	buf := make([]byte, dnsmessage.MaxMsgSize)
	_, err := msg.Pack(buf)
	if err != nil {
		return nil, err
	}

	return buf, nil
}

func parseResponse(buf []byte) (*dnsmessage.Message, error) {
	msg := new(dnsmessage.Message)
	if err := msg.Unpack(buf); err != nil {
		return nil, err
	}

	return msg, nil
}
