package websocketproxy

import (
	"net/http"
	"net/url"
	"github.com/gorilla/websocket"
)

type WebsocketProxy struct {
	Director   func(*http.Request, http.Header)
	Backend    func(*http.Request) *url.URL
	Upgrader   *websocket.Upgrader
	Dialer     *websocket.Dialer
}

func (wp *WebsocketProxy) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	backendURL := wp.Backend(r)
	if backendURL == nil {
		http.Error(w, "No backend URL", http.StatusInternalServerError)
		return
	}

	// Connect to the backend
	connBackend, resp, err := wp.Dialer.Dial(backendURL.String(), nil)
	if err != nil {
		wp.handleConnectionError(w, r, resp, err)
		return
	}
	defer connBackend.Close()

	// Upgrade the client connection to WebSocket
	connClient, err := wp.Upgrader.Upgrade(w, r, nil)
	if err != nil {
		wp.handleConnectionError(w, r, nil, err)
		return
	}
	defer connClient.Close()

	// Setup bidirectional message replication
	errChan := make(chan error)
	go wp.replicateWebsocketConn(connClient, connBackend, errChan)
	go wp.replicateWebsocketConn(connBackend, connClient, errChan)

	// Handle ping/pong messages
	done := make(chan struct{})
	go wp.handlePingPong(connClient, done)
	go wp.handlePingPong(connBackend, done)

	// Wait for errors or completion
	select {
	case <-errChan:
	case <-done:
	}
}

func (wp *WebsocketProxy) replicateWebsocketConn(src, dst *websocket.Conn, errChan chan error) {
	for {
		mt, msg, err := src.ReadMessage()
		if err != nil {
			errChan <- err
			return
		}
		err = dst.WriteMessage(mt, msg)
		if err != nil {
			errChan <- err
			return
		}
	}
}

func (wp *WebsocketProxy) handlePingPong(conn *websocket.Conn, done chan struct{}) {
	ticker := time.NewTicker(time.Second * 30)
	defer ticker.Stop()
	for {
		select {
		case <-ticker.C:
			err := conn.WriteControl(websocket.PingMessage, []byte{}, time.Now().Add(time.Second))
			if err != nil {
				close(done)
				return
			}
		case <-done:
			return
		}
	}
}

func (wp *WebsocketProxy) handleConnectionError(w http.ResponseWriter, r *http.Request, resp *http.Response, err error) {
	if resp != nil && resp.StatusCode >= 500 {
		http.Error(w, "Internal Server Error", http.StatusInternalServerError)
	} else {
		http.Error(w, "Bad Gateway", http.StatusBadGateway)
	}
}
