package main

import (
	"context"
	"fmt"
	"os/exec"
	"time"
)

type proxyCommand struct {
	cmd      *exec.Cmd
	ExtraFiles []*os.File
}

func (p *proxyCommand) Start() error {
	reader, writer, err := os.Pipe()
	if err != nil {
		return fmt.Errorf("failed to create pipe: %w", err)
	}
	defer writer.Close()

	p.cmd = exec.CommandContext(context.Background(), "your_proxy_command")
	p.cmd.ExtraFiles = append(p.cmd.ExtraFiles, writer)

	errChan := make(chan error, 1)
	go func() {
		errChan <- p.cmd.Start()
	}()

	select {
	case startErr := <-errChan:
		if startErr != nil {
			return fmt.Errorf("failed to start proxy: %w", startErr)
		}
	case <-time.After(5 * time.Second):
		return fmt.Errorf("proxy startup timed out")
	}

	buffer := make([]byte, 1024)
	n, err := reader.Read(buffer)
	if err != nil {
		return fmt.Errorf("failed to read proxy initialization status: %w", err)
	}

	status := string(buffer[:n])
	if status != "OK" {
		return fmt.Errorf("unexpected proxy initialization status: %s", status)
	}

	return nil
}
