package main

import (
	"fmt"
	"io"
	"log"
	"net/http"
	"os"
	"time"
)

func helloHandler(w http.ResponseWriter, req *http.Request) {
	fmt.Fprintf(w, "hello")
}

func mirrorHandler(w http.ResponseWriter, req *http.Request) {
	start := time.Now()
	defer log.Printf("Request %s %s took %v", req.Method, req.URL.Path, time.Since(start))

	var requestBody io.Reader = req.Body
	if req.ContentLength == 0 {
		requestBody = nil
	}

	dummyReader := io.NopCloser(requestBody)
	tee := io.TeeReader(dummyReader, os.Stdout)
	req.Body = tee

	fmt.Fprintf(w, "Method: %s\nURL: %s\nHeaders: %v\nBody: ", req.Method, req.URL.Path, req.Header)
	buf := new(bytes.Buffer)
	buf.ReadFrom(tee)
	fmt.Fprint(w, buf.String())
}

func main() {
	http.HandleFunc("/hello", helloHandler)
	http.HandleFunc("/mirror", mirrorHandler)

	go func() {
		log.Println(http.ListenAndServe(":9091", nil))
	}()
	go func() {
