type WebDAVServer struct {
	webdav.Handler
}

func NewWebDAVServer() *WebDAVServer {
	fs := webdav.NewMemFS()
	ls := webdav.NewMemLS()
	return &WebDAVServer{
		Handler: webdav.Handler{
			FileSystem: fs,
			LockSystem: ls,
			Logger: func(req *http.Request, err error) {
				log.Printf("Request: %s %s, Error: %v", req.Method, req.URL.Path, err)
			},
		},
	}
}

func requestHandler(next http.Handler) http.Handler {
	return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
		if r.Header.Get("X-Custom-Header") != "expected-value" {
			http.Error(w, "Unauthorized", http.StatusUnauthorized)
			return
		}
		next.ServeHTTP(w, r)
	})
}

func main() {
	server := NewWebDAVServer()
	wsContainer := restful.NewContainer()
	ws := new(restful.WebService)
	ws.Route(ws.GET("/webdav/{file}").To(server.ServeHTTP))
	wsContainer.Add(ws)
