package main

import (
	"html/template"
	"log"
	"net/http"
)

func serve(addr string) error {
	examples := getExamples()

	tmpl := template.Must(template.ParseFiles("index.html", "example.html"))

	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		tmpl.ExecuteTemplate(w, "index.html", struct {
			Examples []Example
		}{examples})
	})

	http.HandleFunc("/wasm_exec.js", func(w http.ResponseWriter, r *http.Request) {
		http.ServeFile(w, r, "wasm_exec.js")
	})

	http.HandleFunc("/example/", func(w http.ResponseWriter, r *http.Request) {
		path := strings.TrimPrefix(r.URL.Path, "/example/")
		parts := strings.Split(path, "/")
		if len(parts) < 2 {
			http.Error(w, "Invalid example path", http.StatusBadRequest)
			return
		}
		typeName := parts[0]
		link := parts[1]

		example, found := findExample(examples, typeName, link)
		if !found {
			http.NotFound(w, r)
			return
		}

		data := map[string]interface{}{
			"Example": example,
		}

		tmpl.ExecuteTemplate(w, "example.html", data)
	})

	log.Printf("Starting server at %s\n", addr)
	return http.ListenAndServe(addr, nil)
}
