package main

import (
	"crypto/tls"
	"fmt"
	"net/http"
)

func handler(w http.ResponseWriter, req *http.Request) {
	fmt.Fprintf(w, "Hello, World!")
}

func main() {
	http.HandleFunc("/", handler)

	// Start HTTP server
	go func() {
		if err := http.ListenAndServe(":9999", nil); err != nil {
			fmt.Println("HTTP server error:", err)
		}
	}()

	// Start HTTPS server
	tlsConfig := &tls.Config{
		Certificates: []tls.Certificate{loadTLSCertificate()},
	}
	httpsServer := &http.Server{
		Addr:      ":10443",
		TLSConfig: tlsConfig,
	}
	go func() {
		if err := httpsServer.ListenAndServeTLS("", ""); err != nil {
			fmt.Println("HTTPS server error:", err)
		}
	}()

	// Redirect HTTP to HTTPS
	http.HandleFunc("/", func(w http.ResponseWriter, r *http.Request) {
		targetURL := "https://" + r.Host + r.URL.Path
		http.Redirect(w, r, targetURL, http.StatusMovedPermanently)
	})

	fmt.Println("Starting HTTP server on :9999...")
	fmt.Println("Starting HTTPS server on :10443...")
	select {}
}

func loadTLSCertificate() tls.Certificate {
	cert, err := tls.LoadX509KeyPair("cert.pem", "key.pem")
	if err != nil {
		panic(err)
	}
	return cert
}
