package main

import (
	"flag"
	"log"
	"net/http"

	"golang.org/x/net/webdav"
)

func main() {
	port := flag.Int("port", 8080, "Port to listen on")
	flag.Parse()

	memfs := webdav.NewMemFS()
	memls := webdav.NewMemLS(memfs)
	logFunc := func(req *http.Request, err error) {
		if err != nil {
			log.Printf("Error processing %s: %v", req.URL.Path, err)
		} else {
			log.Printf("Processed %s", req.URL.Path)
		}
	}

	handler := &webdav.Handler{
		FileSystem: memfs,
		LockSystem: memls,
		Logger:     logFunc,
	}

	http.Handle("/webdav/", http.StripPrefix("/webdav", handler))

	log.Fatal(http.ListenAndServe(fmt.Sprintf(":%d", *port), nil))
}
