		rw, err := l.Accept()
		if err != nil {
			if !srv.isStarted() {
				return nil
			}
			if neterr, ok := err.(net.Error); ok && neterr.Temporary() {
				continue
			}
			return err
		}
		srv.lock.Lock()
		srv.conns[rw] = struct{}{}
		srv.lock.Unlock()
		wg.Add(1)
		go srv.serveTCPConn(&wg, rw)
	}

	return nil
}

func (srv *Server) serveTCPConn(wg *sync.WaitGroup, rw net.Conn) {
	defer func() {
		srv.lock.Lock()
		delete(srv.conns, rw)
		srv.lock.Unlock()
		rw.Close()
		wg.Done()
	}()

	buf := make([]byte, 4096)
	for {
		n, err := rw.Read(buf)
		if err != nil {
			return
		}

		_, err = rw.Write(buf[:n])
		if err != nil {
			return
		}
	}
}

func (srv *Server) isStarted() bool {
	srv.lock.RLock()
	started := srv.started
	srv.lock.RUnlock()
	return started
}

func unlockOnce(l sync.Locker) func() {
	var once sync.Once
	return func() { once.Do(l.Unlock) }
}

