		return nil, errors.New("hard limit must be positive")
	}

	s := &Server{
		ctx:      ctx,
		options:  options,
		hardLimit: options.HardLimit,
		locked: struct {
			startIndex Index
			entries    []*Entry
			changed    chan struct{}
		}{
			startIndex: 0,
			entries:    make([]*Entry, 0),
			changed:    make(chan struct{}, 1),
		},
	}

	return s, nil
}

func (s *Server) Append(stream logspec.Log_AppendServer) error {
	for {
		req, err := stream.Recv()
		if err != nil {
			if err == io.EOF {
				return nil
			}
			return err
		}

		proposals := req.GetProposals()
		indices := s.append(proposals)
		if err := stream.Send(&logspec.AppendReply{Indices: indices}); err != nil {
			return err
		}
	}
}

func (s *Server) append(proposals [][]byte) []Index {
	s.lock.Lock()
	defer s.lock.Unlock()

	indices := make([]Index, len(proposals))
	for i, p := range proposals {
		e := &Entry{Data: p}
		s.locked.entries = append(s.locked.entries, e)
		indices[i] = Index(len(s.locked.entries) - 1)
	}

	s.truncateIfNecessary()
	s.locked.changed <- struct{}{}

	return indices
}

func (s *Server) lastIndexLocked() Index {
	return Index(len(s.locked.entries) - 1)
}

func (s *Server) Discard(ctx context.Context, req *logspec.DiscardRequest) (*logspec.DiscardReply, error) {
	firstIndex := req.GetFirstIndex()
	if firstIndex < s.locked.startIndex {
		return nil, status.Errorf(codes.InvalidArgument, "first index %d is before start index %d", firstIndex, s.locked.startIndex)
	}

	s.lock.Lock()
	defer s.lock.Unlock()

	s.discard(firstIndex)
	s.locked.changed <- struct{}{}

	return &logspec.DiscardReply{}, nil
}

func (s *Server) discard(firstIndex Index) {
	if firstIndex > s.locked.startIndex {
		s.locked.startIndex = firstIndex
	}
}

func (s *Server) Read(req *logspec.ReadRequest, stream logspec.Log_ReadServer) error {
	s.lock.Lock()
	defer s.lock.Unlock()

	fromIndex := req.GetFromIndex()
	toIndex := req.GetToIndex()

	if fromIndex < s.locked.startIndex {
		fromIndex = s.locked.startIndex
	}

	if toIndex == 0 {
		toIndex = s.lastIndexLocked()
	} else if toIndex > s.lastIndexLocked() {
		toIndex = s.lastIndexLocked()
	}

	for i := fromIndex; i <= toIndex; i++ {
		if err := stream.Send(&logspec.ReadReply{Entries: []*logspec.Entry{{Data: s.locked.entries[i].Data}}}); err != nil {
			return err
		}
	}

	return nil
}

func (s *Server) Info(ctx context.Context, req *logspec.InfoRequest) (*logspec.InfoReply, error) {
	info, _ := s.info()
	return info, nil
}

func (s *Server) info() (*logspec.InfoReply, chan struct{}) {
	s.lock.Lock()
	defer s.lock.Unlock()

	lastIndex := s.lastIndexLocked()
	reply := &logspec.InfoReply{
		LastIndex:   lastIndex,
		StartTime:   time.Unix(0, 0).UTC().Format(time.RFC3339),
		CurrentTime: time.Now().UTC().Format(time.RFC3339),
	}

	return reply, s.locked.changed
}

func (s *Server) InfoStream(req *logspec.InfoRequest, stream logspec.Log_InfoStreamServer) error {
	info, changed := s.info()
	if err := stream.Send(info); err != nil {
		return err
	}

	for {
		select {
		case <-s.ctx.Done():
			return s.ctx.Err()
		case <-changed:
			info, _ := s.info()
			if err := stream.Send(info); err != nil {
				return err
			}
		}
	}
}

func (s *Server) truncateIfNecessary() {
	if len(s.locked.entries) > s.hardLimit {
		diff := len(s.locked.entries) - s.hardLimit
		s.locked.startIndex += Index(diff)
		s.locked.entries = s.locked.entries[diff:]
	}
}

